// import SlotsForm from './SlotsForm'
import './App.css';

class App extends Component {

  constructor() {
    super();
    this.state = {
      error: null,
      isLoaded: false,
      items: [],
      newTanda: {}
    };
  };

  componentDidMount() {
    fetch('/home')
    .then((res) => {
      return res.json();
    })
    .then(
      (result) => {
        this.setState({
        isLoaded: true,
        items: result
      })
