    }).catch((error) => {
      alert(error, "Unable to add apprentice");
    });

  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      console.log(items);
      return (
        /*<ul>
          {items.map(item => (
            <li key={item.firstname}>
              {item.firstname}
            </li>
          ))}
        </ul>*/
        <div>
          <Title name='Tanda' />
          <TandaForm createTanda={this.handleCreateNewTanda} />
        </div>
      );
    }
  }
}

export default App;
