import React, { Component } from 'react';

class TimeGapForm extends Component {
    constructor(props) {
        super(props);

        this.state = {
            timeGap: 0
        };

        this.handleTimeGapChange = this.handleTimeGapChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleTimeGapChange(event) {
        this.setState({ timeGap: parseInt(event.target.value) }, this.handleSubmit);
    }

    handleSubmit = () => {
        this.props.getAmount(this.state.timeGap);
        console.log(this.state.timeGap);
    }


    render() {
        return (
            <form>
                <div onChange={this.handleTimeGapChange}>
                    <li>
                        <input type="radio" value="7" name="amount" /> Weekly
                </li>
                    <li>
                        <input type="radio" value="14" name="amount" /> Every other Week
                </li>
                    <li>
                        <input type="radio" value="30" name="amount" /> Monthly
                </li>

                    <input type="submit" value="Submit" />
                </div>
            </form>
        )
    }
}

export default TimeGapForm;
