import React, { Component } from 'react';
import Title from './Title';
import TandaForm from './TandaForm'
// import MoneyAmountForm from './MoneyAmountForm';
// import TimeGapForm from './TimeGapForm';
// import StartDayForm from './StartDateForm';
// import SlotsForm from './SlotsForm'
import './App.css';

class App extends Component {

  constructor() {
    super();
    this.state = {
      error: null,
      isLoaded: false,
      items: [],
      newTanda: {}
    };
  };

  componentDidMount() {
    fetch('/home')
    .then((res) => {
      return res.json();
    })
    .then(
      (result) => {
        this.setState({
        isLoaded: true,
        items: result
      })
    }).catch(
      (error) => {
        this.setState({
          isLoaded: true,
          error: "Error: Unable to connect to DB"
        })
      console.log(error)
    });
   }

  handleCreateNewTanda = (moneyAmount, timeGap, startDay, slots) => {
    // console.log(`HELLO FROM APP THIS IS WHAT I GOT: 
    // Money Amount: ${ moneyAmount } \n
    // Time Gap: ${ timeGap } \n
    // Start Day: ${ startDay } \n
    // Slots: ${ slots }`);

    let endDay = new Date();
    endDay.setDate(startDay.getDate() + timeGap * (slots - 1) );

    startDay = startDay.toLocaleDateString();
    endDay = endDay.toLocaleDateString();

    console.log(`Start date : ${startDay}`);
    console.log(`End date : ${endDay}`);

    const body = {
      moneyAmount,
      timeGap,
      startDay,
      endDay,
      slots,
      aavailable_slots: 4
    }
    console.log(body);

  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      console.log(items);
      return (
        /*<ul>
          {items.map(item => (
            <li key={item.firstname}>
              {item.firstname}
            </li>
          ))}
        </ul>*/
        <div>
          <Title name='Tanda' />
          <TandaForm createTanda={this.handleCreateNewTanda} />
        </div>
      );
    }
  }
}

export default App;
