import React, { Component } from 'react';

class SlotsForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            slots: 0
        };
        console.log(this.state.amount)
    }

    handleSlotsChange(event) {
        //console.log(event.target.value);
        this.setState({
            slots: event.target.value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(this.state.amount);
    }


    render() {
        return (
            <form onSubmit={this.handleSubmit}>
            <div onChange={this.handleSlotsChange.bind(this)}>
                <li>
                    <input type="radio" value="50" name="amount" /> 5
                </li>
                <li>
                    <input type="radio" value="100" name="amount" /> 10
                </li>
                <li>
                    <input type="radio" value="200" name="amount" /> 12
                </li>

                <input type="submit" value="Submit" />
            </div>
            </form>
        )
    }
}

export default SlotsForm;
